mapper.insert:
INSERT INTO BOOK(BOOK_NO, BOOK_NM, BOOK_DESC, BOOK_PRICE
               , BOOK_STOCK, BOOK_IMG, BOOK_PUB, BOOK_AUTHOR
               , BOOK_PUB_DATE, BOOK_ISBN, BOOK_PAGE)
    VALUES (#{book_no}, #{book_nm}, #{book_desc}, #{book_price}
          , #{book_stock}, #{book_img}, #{book_pub}, #{book_author}
          , #{book_pub_date}, #{book_isbn}, #{book_page})
